// Check for high-value transactions in AI output and send alerts
const items = $input.all();
const item = items[0];
const outputText = item?.json?.output?.toString() || '';

// Check if output mentions order/invoice creation with high amounts
const amountMatch = outputText.match(/(\d{1,3}(?:,\d{3})*(?:\.\d{2})?|\d+)/g);
let highValueDetected = false;
let amount = 0;

if (amountMatch) {
  for (const match of amountMatch) {
    const num = parseFloat(match.replace(/,/g, ''));
    if (num >= 50000) {
      highValueDetected = true;
      amount = num;
      break;
    }
  }
}

// Check if this was a create operation
const isCreate = outputText.toLowerCase().includes('created') || 
                 outputText.toLowerCase().includes('new order') ||
                 outputText.toLowerCase().includes('new invoice');

return [{
  json: {
    has_alert: highValueDetected && isCreate,
    amount: amount,
    original_output: outputText
  }
}];

// Prepare alert message and recipients
const checkData = $('Check_High_Value_Alert').first().json;
const alertConfig = $input.first().json;

const amount = checkData.amount || 0;
const recipients = [];

// Collect all notify IDs from alert configs
if (Array.isArray(alertConfig)) {
  for (const config of alertConfig) {
    if (config.notify_telegram_ids) {
      recipients.push(...config.notify_telegram_ids);
    }
  }
} else if (alertConfig.notify_telegram_ids) {
  recipients.push(...alertConfig.notify_telegram_ids);
}

// Remove duplicates
const uniqueRecipients = [...new Set(recipients)];

if (uniqueRecipients.length === 0) {
  return [{ json: { skip: true } }];
}

const alertMessage = `ðŸš¨ *HIGH-VALUE ALERT*\n\nA transaction of ${amount.toLocaleString()} has been created.\n\nThis exceeds the configured threshold.\n\nPlease review this transaction.`;

return uniqueRecipients.map(id => ({
  json: {
    chat_id: id,
    message: alertMessage
  }
}));

// Extract chart parameters from AI Agent output - flexible format handling
const items = $input.all();
const item = items[0];

let chartParams = null;

if (item && item.json && item.json.output) {
  const outputText = item.json.output.toString();

  // Check for chart marker (with or without brackets)
  if (outputText.includes('CHART_GENERATED') || outputText.includes('CHARTGENERATED')) {
    // Find all JSON objects in the output
    const jsonMatches = outputText.match(/\{[^{}]*\}/g) || [];

    for (const jsonStr of jsonMatches) {
      try {
        const parsed = JSON.parse(jsonStr);
        // Check if it has chart-related keys (with or without underscores)
        if (parsed.chart_type || parsed.charttype || parsed.chart_labels || parsed.chartlabels) {
          chartParams = {
            chart_type: parsed.chart_type || parsed.charttype || 'bar',
            chart_title: parsed.chart_title || parsed.charttitle || 'Chart',
            chart_labels: parsed.chart_labels || parsed.chartlabels || [],
            chart_data: parsed.chart_data || parsed.chartdata || [],
            chart_data_orders: parsed.chart_data_orders || parsed.chartdataorders || null,
            chart_data_invoices: parsed.chart_data_invoices || parsed.chartdatainvoices || null,
            chart_caption: parsed.chart_caption || parsed.chartcaption || parsed.chart_title || parsed.charttitle || 'Chart'
          };
          break;
        }
      } catch (e) { continue; }
    }

    // If no valid JSON, try regex extraction
    if (!chartParams) {
      const typeMatch = outputText.match(/["'](chart_?type)["']\s*:\s*["']([^"']+)["']/i);
      const titleMatch = outputText.match(/["'](chart_?title)["']\s*:\s*["']([^"']+)["']/i);
      const labelsMatch = outputText.match(/["'](chart_?labels)["']\s*:\s*\[([^\]]+)\]/i);
      const dataMatch = outputText.match(/["'](chart_?data)["']\s*:\s*\[([^\]]+)\]/i);
      const ordersMatch = outputText.match(/["'](chart_?data_?orders)["']\s*:\s*\[([^\]]+)\]/i);
      const invoicesMatch = outputText.match(/["'](chart_?data_?invoices)["']\s*:\s*\[([^\]]+)\]/i);
      const captionMatch = outputText.match(/["'](chart_?caption)["']\s*:\s*["']([^"']+)["']/i);

      if (labelsMatch) {
        chartParams = { chart_type: 'bar', chart_title: 'Chart', chart_labels: [], chart_data: [] };
        if (typeMatch) chartParams.chart_type = typeMatch[2];
        if (titleMatch) chartParams.chart_title = titleMatch[2];
        if (captionMatch) chartParams.chart_caption = captionMatch[2];
        try { chartParams.chart_labels = JSON.parse('[' + labelsMatch[2] + ']'); } catch(e) { chartParams.chart_labels = labelsMatch[2].split(',').map(s => s.trim().replace(/["']/g, '')); }
        if (dataMatch) { try { chartParams.chart_data = JSON.parse('[' + dataMatch[2] + ']').map(Number); } catch(e) {} }
        if (ordersMatch) { try { chartParams.chart_data_orders = JSON.parse('[' + ordersMatch[2] + ']').map(Number); } catch(e) {} }
        if (invoicesMatch) { try { chartParams.chart_data_invoices = JSON.parse('[' + invoicesMatch[2] + ']').map(Number); } catch(e) {} }
      }
    }
  }
}

if (chartParams && chartParams.chart_labels && chartParams.chart_labels.length > 0) {
  // Determine if we have dual datasets
  const hasOrders = chartParams.chart_data_orders && chartParams.chart_data_orders.length > 0;
  const hasInvoices = chartParams.chart_data_invoices && chartParams.chart_data_invoices.length > 0;
  const hasSingleData = chartParams.chart_data && chartParams.chart_data.length > 0;

  return [{
    json: {
      has_chart: true,
      chart_type: chartParams.chart_type || 'bar',
      chart_title: chartParams.chart_title || 'Chart',
      chart_labels: chartParams.chart_labels,
      chart_data: hasSingleData ? chartParams.chart_data : (hasOrders ? chartParams.chart_data_orders : []),
      chart_data_orders: hasOrders ? chartParams.chart_data_orders : null,
      chart_data_invoices: hasInvoices ? chartParams.chart_data_invoices : null,
      chart_caption: chartParams.chart_caption || chartParams.chart_title || 'Chart',
      dual_dataset: hasOrders && hasInvoices
    }
  }];
}

return [{ json: { has_chart: false } }];

