// Preserve chart metadata (caption, title) with binary image
// Merge binary from Generate_Chart_Image with JSON from Extract Chart Parameters
const currentItems = $input.all();
const extractParamsData = $('Extract Chart Parameters').all();

const result = [];

for (let i = 0; i < currentItems.length; i++) {
  const currentItem = currentItems[i];
  const paramItem = extractParamsData[i] || extractParamsData[0] || {};

  // Get metadata from Extract Chart Parameters node
  const chartCaption = paramItem.json?.chart_caption || paramItem.json?.chart_title || 'Chart';
  const chartTitle = paramItem.json?.chart_title || 'Chart';

  result.push({
    json: {
      chart_caption: chartCaption,
      chart_title: chartTitle
    },
    binary: currentItem.binary || {}
  });
}

return result;

// Check if chart binary exists from Generate_Chart_Image node
// Only outputs if binary exists, otherwise stops the flow
const items = $input.all();

let chartImage = null;
let chartCaption = 'Chart';

// Check input items for binary chart_image
for (const item of items) {
  if (item.binary && item.binary.chart_image) {
    chartImage = item.binary.chart_image;
    if (item.json && item.json.chart_caption) {
      chartCaption = item.json.chart_caption;
    } else if (item.json && item.json.chart_title) {
      chartCaption = item.json.chart_title;
    }
    break;
  }
}

// Only return output if chart exists, otherwise return empty to stop flow
if (chartImage) {
  return [{
    json: {
      has_chart: true,
      chart_caption: chartCaption
    },
    binary: {
      chart_image: chartImage
    }
  }];
}

// Return empty array to stop the flow (no chart to send)
return [];

// Detect export markers and extract filter parameters
const items = $input.all();
const item = items[0];
const outputText = item?.json?.output?.toString() || '';

// Check for export markers: [EXPORT_ORDERS] or [EXPORT_INVOICES]
const ordersMatch = outputText.match(/\[EXPORT_ORDERS\]|EXPORT_ORDERS/i);
const invoicesMatch = outputText.match(/\[EXPORT_INVOICES\]|EXPORT_INVOICES/i);

if (ordersMatch || invoicesMatch) {
  const exportType = invoicesMatch ? 'invoices' : 'orders';

  // Extract optional filters from the marker line
  // Format: [EXPORT_ORDERS supplier=Acme from=2024-01-01 to=2024-12-31]
  let supplier = null;
  let dateFrom = null;
  let dateTo = null;

  const supplierMatch = outputText.match(/supplier=([\w\s]+?)(?:\s+(?:from|to)=|\]|$)/i);
  const fromMatch = outputText.match(/from=([\d-]+)/i);
  const toMatch = outputText.match(/to=([\d-]+)/i);

  if (supplierMatch) supplier = supplierMatch[1].trim();
  if (fromMatch) dateFrom = fromMatch[1];
  if (toMatch) dateTo = toMatch[1];

  // Return parsed export request for the next node to fetch data
  return [{
    json: {
      has_export: true,
      export_type: exportType,
      filters: {
        supplier: supplier,
        date_from: dateFrom,
        date_to: dateTo
      }
    }
  }];
}

// No export detected
return [{ json: { has_export: false } }];

// Generate CSV from fetched data
const items = $input.all();
let records = [];

// Collect all records from input
for (const item of items) {
  if (Array.isArray(item.json)) {
    records = records.concat(item.json);
  } else if (item.json && typeof item.json === 'object') {
    records.push(item.json);
  }
}

if (records.length === 0) {
  return [{ json: { error: 'No data to export', record_count: 0 } }];
}

// Detect type from first record's fields
const isInvoice = records[0].hasOwnProperty('invoice_date') || records[0].hasOwnProperty('financing_type');
const exportType = isInvoice ? 'invoices' : 'orders';

// Generate CSV
let csv = '';

if (isInvoice) {
  csv = 'ID,Supplier,Invoice Date,Total Amount,Net Amount,Exchange Rate,Financing Type\n';
  for (const r of records) {
    const supplier = (r.supplier || '').toString().replace(/,/g, ';').replace(/"/g, '""');
    csv += `"${r.id || ''}","${supplier}","${r.invoice_date || ''}",${r.total_amount || 0},${r.net_amount || 0},${r.exchange_rate || 1},"${(r.financing_type || '').replace(/"/g, '""')}"\n`;
  }
} else {
  csv = 'ID,Supplier,Order Date,Total Amount,Net Amount,Source Channel\n';
  for (const r of records) {
    const supplier = (r.supplier || '').toString().replace(/,/g, ';').replace(/"/g, '""');
    csv += `"${r.id || ''}","${supplier}","${r.order_date || ''}",${r.total_amount || 0},${r.net_amount || 0},"${(r.source_channel || '').replace(/"/g, '""')}"\n`;
  }
}

const filename = `${exportType}_export_${new Date().toISOString().split('T')[0]}.csv`;

// Return with binary data
return [{
  json: {
    export_type: exportType,
    record_count: records.length,
    filename: filename
  },
  binary: {
    data: await this.helpers.prepareBinaryData(Buffer.from(csv, 'utf-8'), filename, 'text/csv')
  }
}];
